    Statement stmt = null;
    Connection conn = null;
    ResultSet rs = null;
    try {
        Class.forName("com.mysql.jdbc.Driver"); //加载JDBC驱动，成功后会自动将Driver类的实例注册到DriverManager中
        conn = DriverManager.getConnection( 
                  "jdbc:MySql://192.168.192.125:3358/spring_boot_test?useUnicode=true", // URL
                  "root", // 用户名
